package com.victor.sign;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.util.Log;import android.widget.Toast;import com.victor.sign.util.AppUtil;import com.victor.sign.util.SignVerificationUtil;public class MainActivity extends AppCompatActivity {    private String TAG = "MainActivity";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        int signHashCode = AppUtil.getSignature(this);        boolean checkSign = SignVerificationUtil.checkSignature(signHashCode,this);        if (checkSign) {            Toast.makeText(getApplicationContext(),"签名验证成功！",Toast.LENGTH_SHORT).show();        } else {            Toast.makeText(getApplicationContext(),"签名验证失败！",Toast.LENGTH_SHORT).show();        }    }}