package com.victor.sign.util;import android.content.Context;import android.content.pm.PackageInfo;import android.content.pm.PackageManager;import android.content.pm.Signature;import android.util.Log;/** * Created by victor on 2017/3/15. */public class AppUtil {    private static String TAG = "AppUtil";    public static int getSignature(Context context) {        int signHashCode = -1;        try {            PackageInfo packageInfo = context.getPackageManager().getPackageInfo(                    context.getPackageName(), PackageManager.GET_SIGNATURES);            Signature[] signs = packageInfo.signatures;            Signature sign = signs[0];            signHashCode = sign.hashCode();        } catch (Exception e) {            e.printStackTrace();        }        Log.e(TAG,"signHashCode = " + signHashCode);        return signHashCode;    }}